<template>
  <img @click="handleClick" class="mint-image" :src="src" :style="computeStyle" />
</template>

<script>
export default {
  name: 'mt-image',
    methods: {
        handleClick(evt) {
            this.$emit('click', evt);
        }
    },
  props: {
      src: String,
      width: String,
      height: String
  },
    computed: {
        computeStyle: function() {
            let width = this.width;
            let height = this.height;
            let _style = '';
            if (width) {
                _style += 'width:' + width + ';';
            }
            if (height) {
                _style += 'height:' + height + ';';
            }
            return _style;
        }
    }
};
</script>

<style lang="css">
  @import "../../../src/style/var.css";

  @component-namespace mint {
    @component image {
      position: relative;
      width: 100%;
    }
  }
</style>
